code_example:
// Basic class definition
public class Person {
    // Private fields
    private String name;
    private int age;

    // Constructor
    public Person(String name, int age) {
        this.name = name;
        this.age = age;
    }

    // Getter methods
    public String getName() {
        return name;
    }

    public int getAge() {
        return age;
    }

    // Setter methods
    public void setName(String name) {
        this.name = name;
    }

    public void setAge(int age) {
        if (age > 0) {
            this.age = age;
        }
    }

    // Method
    public void introduce() {
        System.out.println("Hi, I'm " + name + " and I'm " + age);
    }

    public boolean isAdult() {
        return age >= 18;
    }
}

// Creating and using objects
Person person1 = new Person("Alice", 25);
Person person2 = new Person("Bob", 17);

person1.introduce();  // Hi, I'm Alice and I'm 25
System.out.println(person1.isAdult());  // true
System.out.println(person2.isAdult());  // false

// Inheritance
public class Student extends Person {
    private String studentId;
    private double gpa;

    public Student(String name, int age, String studentId) {
        super(name, age);  // Call parent constructor
        this.studentId = studentId;
    }

    public void study() {
        System.out.println(getName() + " is studying");
    }

    @Override
    public void introduce() {
        super.introduce();
        System.out.println("Student ID: " + studentId);
    }
}

// Static fields and methods
public class MathUtils {
    public static final double PI = 3.14159;

    public static int add(int a, int b) {
        return a + b;
    }
}

// Usage of static members
System.out.println(MathUtils.PI);
int sum = MathUtils.add(5, 3);

common_mistakes.wrong:
public class Person {
    public String name;
}

common_mistakes.right:
public class Person {
    private String name;
    public String getName() { return name; }
}

common_mistakes.wrong:
class Student extends Person {
    public Student(String name) {
        // Missing super(name)
    }
}

common_mistakes.right:
class Student extends Person {
    public Student(String name) {
        super(name);
    }
}
