syntax.pattern:
class ClassName:
    def __init__(self, parameters):
        self.attribute = value

    def method(self):
        # code

examples:
class Person:
    def __init__(self, name):
        self.name = name

class Dog:
    def __init__(self, name, age):
        self.name = name
        self.age = age

    def bark(self):
        return 'Woof!'

common_mistakes.wrong:
class Student(Person):
    def __init__(self, name, grade):
        self.grade = grade

common_mistakes.right:
class Student(Person):
    def __init__(self, name, grade):
        super().__init__(name)
        self.grade = grade

common_mistakes.wrong:
class Person:
    def __init__(self, name):
        self.name = name
    def greet(self):
        return f'Hi {name}'

common_mistakes.right:
class Person:
    def __init__(self, name):
        self.name = name
    def greet(self):
        return f'Hi {self.name}'
