syntax.pattern:
if (condition) {
    // code
} else if (condition) {
    // code
} else {
    // code
}

examples:
if (score >= 90) {
    grade = "A";
} else if (score >= 80) {
    grade = "B";
} else {
    grade = "C";
}

code_example:
// Basic if statement
int age = 20;
if (age >= 18) {
    System.out.println("Adult");
}

// if-else if-else
int score = 85;
String grade;
if (score >= 90) {
    grade = "A";
} else if (score >= 80) {
    grade = "B";
} else if (score >= 70) {
    grade = "C";
} else {
    grade = "F";
}
System.out.println("Grade: " + grade);

// Ternary operator
String status = (score >= 50) ? "Pass" : "Fail";

// Nested conditions
if (age >= 18) {
    if (hasLicense) {
        System.out.println("Can drive");
    } else {
        System.out.println("Need license");
    }
} else {
    System.out.println("Too young");
}

// Multiple conditions
boolean canEnter = (age >= 18 && hasID);
boolean isDayOff = (isWeekend || isHoliday);

// Switch statement
String day = "Monday";
switch (day) {
    case "Monday":
        System.out.println("Start of week");
        break;
    case "Friday":
        System.out.println("End of week");
        break;
    case "Saturday":
    case "Sunday":
        System.out.println("Weekend");
        break;
    default:
        System.out.println("Midweek");
}

// Switch expression (Java 12+)
String dayType = switch (day) {
    case "Saturday", "Sunday" -> "Weekend";
    case "Monday" -> "Start of week";
    default -> "Weekday";
};

common_mistakes.right:
switch(x) {
    case 1:
        System.out.println("One");
        break;
    case 2:
        System.out.println("Two");
        break;
}
