def is_palindrome(s: str) -> bool:
    if s is None:
        return False
    normalized = s.lower()
    reversed_s = normalized[::-1]
    return normalized == reversed_s

if __name__ == "__main__":
    test = "Level"
    result = is_palindrome(test)
    print(f"{test} -> {result}")
